    | Get Name
    |--------------------------------------------------------------------------
    |
    | Returns random name
    |
    */
    public static function getName
    (
        $gender='*',
        $name_starting_with="*",
        $surname_starting_with="*",
        $capitalize=true
    )
    {
        if($gender=="*"||$gender=="m"||$gender=="f" ){
            $config=[
                'name_starting_with'=>$name_starting_with,
                'surname_starting_with'=>$surname_starting_with,
                'gender'=>$gender,
                'capitalize'=>$capitalize
            ];
            $first_name=self::getFirstName($config['gender'],$config['name_starting_with'],$config['capitalize']);

            $surname=self::getSurname($config['surname_starting_with'],$config['capitalize']);
            $full_name=$first_name.' '.$surname;
            return $full_name;
        }else{
            dd('Parameter not supported');
        }


    }

    /*
    |--------------------------------------------------------------------------
    | Get First Name
    |--------------------------------------------------------------------------
    |
    | Returns random first name
    |
    */
    public static function getFirstName($gender="*",$start="*",$capitalize=true)
    {
        if(($gender=="*"||$gender=="m"||$gender=="f") && is_bool($capitalize)){
            if($gender=='*'){
                $all_names=array_merge(self::$_boys_names, self::$_girls_names);
            }elseif($gender=='m'){
                $all_names=self::$_boys_names;

            }
            elseif($gender=='f'){
                $all_names=self::$_girls_names;
            }
            if($start!='*'){
                $preg="/^".$start."(\w+)/i";
                $all_names  = preg_grep ($preg, $all_names);
            }
            if(count($all_names)>=1){
                $random_key = array_rand($all_names);
                if($capitalize)
                    return ucfirst($all_names[$random_key]);
                return $all_names[$random_key];
            }
        }else{
            dd('Parameter not supported');

        }
    }

    /*
    |--------------------------------------------------------------------------
    | Get Surname
    |--------------------------------------------------------------------------
    |
    | Returns random surname
    |
    */
    public static function getSurname($start="*",$capitalize=true)
    {
        if(is_bool($capitalize)){
            $all_surnames=self::$_surnammes;
            if($start!='*'){
                $preg="/^".$start."(\w+)/i";
                $all_surnames  = preg_grep ($preg, $all_surnames);
            }
            if(count($all_surnames)>=1){
                $random_key = array_rand($all_surnames);
                if($capitalize)
                    return ucfirst($all_surnames[$random_key]);
                return $all_surnames[$random_key];

            }

        }else{

        }
    }
    public static function getEmail()
    {
        $all_names=array_merge(self::$_boys_names, self::$_girls_names);
        $surnames=self::$_surnammes;
        $mid_char=['.','.','.','-','_','_','_','_','_','-','.','',''];
        $ranbom_bool=[0,0,0,0,0,1,1];


        $random_key_name = array_rand($all_names);
        $random_key_surname = array_rand($surnames);
        $random_key_mid_char = array_rand($mid_char);

        //Part One
            $part_1=$all_names[$random_key_name];
        //Part Two
            $part_2=$mid_char[$random_key_mid_char];
        //Part Thee
            $part_3='';
            $is_short_form=$ranbom_bool[array_rand($ranbom_bool)];
            if($is_short_form){
                $part_3=$surnames[$random_key_surname][0];
            }else{
                $part_3=$surnames[$random_key_surname];
            }

        //Part Four
            $part_4='';
            $is_rand=$ranbom_bool[array_rand($ranbom_bool)];
            if($is_rand){
                $part_4=rand(0,999);
            }
        //Part Five (Domin)
            $domain='@'.self::getRandomString(5).'.'.self::getRandomString(3);
            $email=$part_1.$part_2.$part_3.$part_4.$domain;

        return $email;


    }
    public static function getUsername(){
        $all_names=array_merge(self::$_boys_names, self::$_girls_names);
        $surnames=self::$_surnammes;
        $mid_char=['_','_','_','_','_','','',''];
        $ranbom_bool=[0,0,0,0,0,1,1,1];


        $random_key_name = array_rand($all_names);
        $random_key_surname = array_rand($surnames);
        $random_key_mid_char = array_rand($mid_char);

        //Part One
            $part_1=$all_names[$random_key_name];
        //Part Two
            $part_2=$mid_char[$random_key_mid_char];
        //Part Thee
            $part_3='';
            $is_short_form=$ranbom_bool[array_rand($ranbom_bool)];
            if($is_short_form){
                $part_3=$surnames[$random_key_surname][0];
            }else{
                $part_3=$surnames[$random_key_surname];
            }

        //Part Four
            $part_4='';
            $is_rand=$ranbom_bool[array_rand($ranbom_bool)];
            if($is_rand){
                $part_4=rand(0,999);
            }

            $username=$part_1.$part_2.$part_3.$part_4;

        return $username;

    }
    public static function getMobileNumber(){
        $prefix=[984,980];
        $number=self::getRandomNumber(7);

        return $prefix[array_rand($prefix)].$number;
    }

    public static function getRandomString($length = 3) {
        $characters = 'abcdefghijklmnopqrstuvwxyz';
        $string = '';

        for ($i = 0; $i < $length; $i++) {
            $string .= $characters[mt_rand(0, strlen($characters) - 1)];
        }

        return $string;
    }
    public static function getRandomNumber($length = 3) {
        $characters = '1234567890';
        $string = '';
        for ($i = 0; $i < $length; $i++) {
            $string .= $characters[mt_rand(0, strlen($characters) - 1)];
        }
        return $string;
    }
}
